import subprocess

HSMS = "hsms -y --nochunks " #Sign unsigned bundles
commandSign= HSMS + "/home/leo/Crypto/chia/internal-custody/keys_and_sb/1.se"

try:
    unsigned_bundle =""
    proc = subprocess.Popen(commandSign, shell = True, stdin=subprocess.PIPE, stdout=subprocess.PIPE)
    proc.stdin.write(bytes(unsigned_bundle,'utf-8'))
    proc.stdin.close()
    signed_bundle = proc.stdout.read()
    print("\n")
    print(signed_bundle.decode('utf-8'))
except Exception as e:
    print(repr(e))
